result:
query($slug: String!) {
  strapi {
    categories(where: { slug: $slug }) {
      name
      articles(where: { status: "published" }) {
        id
        title
        content
        slug
        image {
          url
        }
        category {
          name
        }
        author {
          name
          picture {
            url
          }
        }
      }
    }
  }
}
